import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Modal from 'react-modal';
 
const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};
 

function AddModal({isOpen, closeModal}){


  const [title, setTitle] = useState('')

  const [year, setYear] = useState(null)

  const [category, setCategory] = useState('')

  const [rating, setRating] = useState('')

  const [image, setImage] = useState('')

  

  return (
    <div>
      <Modal
        isOpen={isOpen}

        style={customStyles}
        contentLabel="Example Modal"
      >
        <form>
          <div class="mb-3">
            <label class="form-label">Title</label>
            <input type="text" class="form-control" id="exampleInputEmail1"/>
          </div>

          <div class="mb-3">
            <label class="form-label">Year</label>
            <input type="number" min="1900" max="2021" class="form-control" id="exampleInputEmail1"/>
          </div>

          <div class="mb-3">
            <label class="form-label">Rating</label>
            <input type="number" min="1" max="5" class="form-control" id="exampleInputPassword1"/>
          </div>

          <div class="mb-3">
            <label class="form-label">Category</label>
            <input type="text" class="form-control" id="exampleCheck1"/>
          </div>

          <div class="mb-3">
            <label class="form-label">Image URL</label>
            <input type="text" class="form-control" id="exampleCheck1"/>
          </div>

          <button type="submit" class="btn btn-primary">Submit</button>
        </form>

        <button onClick={closeModal}>close</button>

      </Modal>
    </div>
  );
}
 
export default AddModal;
